changeSet.sql:
CREATE OR REPLACE FUNCTION balance_audit_trigger()
RETURNS TRIGGER AS $$
    BEGIN
        IF (TG_OP = 'UPDATE') THEN
        INSERT INTO account_audit (account_number, balance, action, timestamp)
        VALUES (NEW.account_id, NEW.account_number, NEW.balance, 'UPDATE', NOW());
    END IF;
RETURN NEW;
    END;
$$ LANGUAGE plpgsql;

CREATE TRIGGER balance_audit
AFTER UPDATE ON accounts
FOR EACH ROW
EXECUTE FUNCTION balance_audit_trigger();
